</template>

<script setup lang="ts">
const sectionEl = ref<HTMLElement | null>(null)
const imageEl = ref<HTMLElement | null>(null)
const contentEl = ref<HTMLElement | null>(null)

const { gsap, ScrollTrigger } = useGsap()
const { prefersReducedMotion } = useReducedMotion()

const handleImageError = (event: Event) => {
  const img = event.target as HTMLImageElement
  console.error('Image failed to load:', img.src)
  // Fallback to a different image if needed
  img.src = '/media/1.jpg'
}

const keyPoints = [
  'Certified (RYT 200+) — private & group sessions',
  'Workshops and retreats in nature'
]

const statsEl = ref<HTMLElement | null>(null)
const displayTrainees = ref(0)
const displayYears = ref(0)
const displayStyles = ref(0)

onMounted(() => {
  if (!gsap || !ScrollTrigger) return
  if (prefersReducedMotion.value) return

  let ctx: any

  ctx = gsap.context(() => {
    // Image animation
    if (imageEl.value) {
      const imageContainer = imageEl.value.querySelector('.image-container')
      const trainerImage = imageEl.value.querySelector('.trainer-image')

      gsap.fromTo(
        imageContainer,
        {
          opacity: 0,
          scale: 0.96,
          y: 40
        },
        {
          opacity: 1,
          scale: 1,
          y: 0,
          duration: 1,
          ease: 'power3.out',
          scrollTrigger: {
            trigger: imageEl.value,
            start: 'top 85%',
            end: 'top 60%',
            toggleActions: 'play none none reverse'
          }
        }
      )

      // Subtle hover effect
      if (trainerImage && imageContainer) {
        imageContainer.addEventListener('mouseenter', () => {
          gsap.to(trainerImage, {
            scale: 1.03,
            duration: 0.5,
            ease: 'power2.out'
          })
        })

        imageContainer.addEventListener('mouseleave', () => {
          gsap.to(trainerImage, {
            scale: 1,
            duration: 0.5,
            ease: 'power2.out'
          })
        })
      }
    }

    // Content animations
    if (contentEl.value) {
      const heading = contentEl.value.querySelector('h3')
      const texts = contentEl.value.querySelectorAll('.about-text')
      const keyPoints = contentEl.value.querySelectorAll('.key-point')
      const stats = contentEl.value.querySelectorAll('.stat-item')

      // Heading
      if (heading) {
        gsap.fromTo(
          heading,
          {
            opacity: 0,
            y: 20
          },
          {
            opacity: 1,
            y: 0,
            duration: 0.8,
            ease: 'power2.out',
            scrollTrigger: {
              trigger: contentEl.value,
              start: 'top 85%',
              end: 'top 70%',
              toggleActions: 'play none none reverse'
            }
          }
        )
      }

      // Text paragraphs
      gsap.fromTo(
        texts,
        {
          opacity: 0,
          y: 15
        },
        {
          opacity: 1,
          y: 0,
          duration: 0.7,
          stagger: 0.1,
          ease: 'power2.out',
          scrollTrigger: {
            trigger: contentEl.value,
            start: 'top 80%',
            end: 'top 60%',
            toggleActions: 'play none none reverse'
          }
        }
      )

      // Key points
      gsap.fromTo(
        keyPoints,
        {
          opacity: 0,
          x: -15
        },
        {
          opacity: 1,
          x: 0,
          duration: 0.6,
          stagger: 0.08,
          ease: 'power2.out',
          scrollTrigger: {
            trigger: contentEl.value,
            start: 'top 75%',
            end: 'top 55%',
            toggleActions: 'play none none reverse'
          }
        }
      )

      // Stats: entrance + count-up
      gsap.fromTo(
        stats,
        { opacity: 0, scale: 0.9, y: 12 },
        {
          opacity: 1,
          scale: 1,
          y: 0,
          duration: 0.5,
          stagger: 0.08,
          ease: 'back.out(1.2)',
          scrollTrigger: {
            trigger: statsEl.value,
            start: 'top 88%',
            toggleActions: 'play none none reverse'
          }
        }
      )
      const count = { t: 0, y: 0, s: 0 }
      gsap.to(count, {
        t: 500,
        y: 10,
        s: 4,
        duration: 1.8,
        ease: 'power2.out',
        snap: { t: 1, y: 1, s: 1 },
        onUpdate: () => {
          displayTrainees.value = Math.round(count.t)
          displayYears.value = Math.round(count.y)
          displayStyles.value = Math.round(count.s)
        },
        scrollTrigger: {
          trigger: statsEl.value,
          start: 'top 85%',
          toggleActions: 'play none none reverse'
        }
      })
    }
  }, sectionEl)

  onBeforeUnmount(() => {
    ctx?.revert()
  })
})
</script>

