const { el: revealEl, isVisible: isRevealed } = useScrollReveal({ threshold: 0.1 })
const images = [
  { src: '/media/1.jpg', alt: 'Yoga session' },
  { src: '/media/4.jpg', alt: 'Practice in nature' },
